import React from 'react';
import './Services.css';

const Services = () => {
  const services = [
    {
      icon: 'üåê',
      title: 'Sites Web',
      description: 'Sites web modernes et responsives avec les derni√®res technologies web.',
      features: ['Design responsive', 'SEO optimis√©', 'Performance √©lev√©e', 'Maintenance incluse']
    },
    {
      icon: 'üì±',
      title: 'Applications Mobiles',
      description: 'Applications natives et hybrides pour iOS et Android.',
      features: ['Interface native', 'Performance optimale', 'S√©curit√© renforc√©e', 'Mises √† jour r√©guli√®res']
    },
    {
      icon: 'üõí',
      title: 'E-commerce',
      description: 'Plateformes de vente en ligne compl√®tes et s√©curis√©es.',
      features: ['Gestion des produits', 'Paiements s√©curis√©s', 'Gestion des stocks', 'Analytics int√©gr√©s']
    },
    {
      icon: 'üé®',
      title: 'Design UI/UX',
      description: 'Interfaces utilisateur intuitives et exp√©riences utilisateur exceptionnelles.',
      features: ['Wireframes', 'Prototypes', 'Tests utilisateurs', 'Design system']
    }
  ];

  return (
    <section id="services" className="services">
      <div className="container">
        <div className="section-header text-center">
          <h2>Nos Services</h2>
          <p>Des solutions compl√®tes pour tous vos besoins num√©riques</p>
        </div>
        
        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card card fade-in">
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p className="service-description">{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>{feature}</li>
                ))}
              </ul>
              <div className="service-cta">
                <a href="#contact" className="btn btn-outline">
                  En savoir plus
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
